var endDate = new Date("Sep 03, 2019 10:00:00").getTime();

var timer = setInterval(function() {

    let now = new Date().getTime();
    let t = endDate - now;
    
    if (t >= 0) {
    
        let days = Math.floor(t / (1000 * 60 * 60 * 24));
        let hours = Math.floor((t % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
        let mins = Math.floor((t % (1000 * 60 * 60)) / (1000 * 60));
        let secs = Math.floor((t % (1000 * 60)) / 1000);
    
        document.getElementById("dias").innerHTML = days;
    
        document.getElementById("horas").innerHTML = ("00"+hours).slice(-2);
    
        document.getElementById("min").innerHTML = ("00"+mins).slice(-2);
    
        document.getElementById("seg").innerHTML = ("00"+secs).slice(-2);
    
    } else {


    
    }
    
}, 1000);